        "Titelbild": "merz-titel.jpeg (https://v5.airtableusercontent.com/v1/17/17/1685498400000/dlJb1kzE6YwSWSw06nVSWw/xBHVPwJ7DEyoSMVukf7SyzZHPoDUkUadWX6-CD6RvfgjHqi65dWLCymrXmfxiQ9elDWPiwjEKpeSnlILMXY75rbYSEd1e6h-CErw44iVFjQ/RXZNhrSYssEsXeYgm69xKQc7lkM9I_NuU6etyP9It1A)",
